        if(!(await window.tronWeb) || !(await window.tronWeb.ready)){
          this.setState({TronLinkValue: 1});
          isClicked = true;
          return;
      }
    }
        await this.fetchData();
        if(this.state.TronLinkValue==1){return;}
        await this.fetchYourData();
        isClicked = false;
    }

