import React from 'react';
import TronLinkGuide from 'components/TronLinkGuide';
import TronWeb from 'tronweb';
import Utils from 'utils';
import Swal from 'sweetalert2';


import './App.scss';

const FOUNDATION_ADDRESS = 'TWiWt5SEDzaEqS6kE5gandWMNfxR2B5xzg';

////////////////////////////////////////////////////////////////////////////////////
const contractAddress = 'TWqFZbEqEEixWz6XntdpJwW2Hsv3bYhgb9';   /// Add your contract address here
////////////////////////////////////////////////////////////////////////////////////

class App extends React.Component {

    constructor(props) {
        super(props);
        this.state = {

              tronWeb: {
                  installed: false,
                  loggedIn: false
              },
              Players: '...',
              Invested: '...',
              PaidOut: '...',
              Animals: '...',
              Address: '...',
              ivch: '',
              ivp: '',
              ivsh: '',
              ivco: '',
              ivge: ''

            }
        // this.changeSide = this.changeSide.bind(this)
        // this.init = this.init.bind(this)
        // this.updateBetValue = this.updateBetValue.bind(this)
        // document.getElementById('name-0').textContent = 'Player1';
        // document.getElementById('name-1').textContent = 'Player2';
        // document.querySelector('.player-0-panel').classList.remove('active');
        // document.querySelector('.player-1-panel').classList.remove('active');
        // document.querySelector('.player-0-panel').classList.add('active');
        // document.querySelector('.dice').classList.remove('displaynone');
        // cube.classList.add('animation-1');
        // document.getElementById("dice-game").style.display = "block";
        // document.getElementById("roll-button").style.display = "block";
        // <button className="btn btn-primary" onClick={(event) => {event.preventDefault()
        //                                                   this.init()}  }>Start New game </button>

                //  <input style={{ width:"100px" }} value='0' onChange={this.updateBetValue}/>
                //
                // <button className="btn-roll" onClick={(event) => {event.preventDefault()
                //                     this.roll()}  }><i className="fas fa-dice fa-5x"></i></button>
    }

    async componentDidMount() {

        this.setState({loading:true})
        await new Promise(resolve => {
            const tronWebState = {
                installed: !!window.tronWeb,
                loggedIn: window.tronWeb && window.tronWeb.ready
            };

            if(tronWebState.installed) {
                this.setState({
                    tronWeb:
                    tronWebState
                });

                return resolve();
            }

            let tries = 0;

            const timer = setInterval(() => {
                if(tries >= 10) {
                    const TRONGRID_API = 'https://api.trongrid.io';

                    window.tronWeb = new TronWeb(
                        TRONGRID_API,
                        TRONGRID_API,
                        TRONGRID_API
                    );

                    this.setState({
                        tronWeb: {
                            installed: false,
                            loggedIn: false
                        }
                    });

                    clearInterval(timer);
                    return resolve();
                }

                tronWebState.installed = !!window.tronWeb;
                tronWebState.loggedIn = window.tronWeb && window.tronWeb.ready;

                if(!tronWebState.installed)
                    return tries++;

                this.setState({
                    tronWeb: tronWebState
                });

                resolve();
            }, 100);
        });

        if(!this.state.tronWeb.loggedIn) {
            // Set default address (foundation address) used for contract calls
            // Directly overwrites the address object as TronLink disabled the
            // function call
            window.tronWeb.defaultAddress = {
                hex: window.tronWeb.address.toHex(FOUNDATION_ADDRESS),
                base58: FOUNDATION_ADDRESS
            };

            window.tronWeb.on('addressChanged', () => {
                if(this.state.tronWeb.loggedIn)
                    return;

                this.setState({
                    tronWeb: {
                        installed: true,
                        loggedIn: true
                    }
                });
            });
        }

        await Utils.setTronWeb(window.tronWeb, contractAddress);


    }
    async fetchData(){
      const result1 = (await Utils.contract.totalPlayers().call()).toNumber();
      const result2 = (await Utils.contract.totalInvested().call()).toNumber();
      const result3 = (await Utils.contract.totalPayout().call()).toNumber();
      const result4 = (await Utils.contract.totalAnimals().call()).toNumber();
      var result5 = Utils.tronWeb.address.fromHex(((await Utils.tronWeb.trx.getAccount()).address).toString());
      result5 = result5.slice(0, 10);
      this.setState({
        Players: result1,
        Invested: result2,
        PaidOut: result3,
        Animals: result4,
        Address: result5 + '...'
      });
    }
    play(){
        if(!!window.tronWeb && window.tronWeb.ready){
        document.querySelector('.forButton').classList.add('dnone');
        document.querySelector('.game').classList.remove('dnone');}

        this.fetchData();
    }
    handleChange(event) {
      if(event.target.name == 'chnumber'){
        this.state.ivch = event.target.value;
      }else if(event.target.name == 'pgnumber'){
        this.state.ivpg = event.target.value;
      }else if(event.target.name == 'shnumber'){
        this.state.ivsh = event.target.value;
      }else if(event.target.name == 'conumber'){
        this.state.ivco = event.target.value;
      }else {
        this.state.ivge = event.target.value;
      }
    }

    render() {
        if(!this.state.tronWeb.installed)
            return <TronLinkGuide />;

        if(!this.state.tronWeb.loggedIn)
            return <TronLinkGuide installed />;

        return (
          <div className = "allReact">
          <div className = "forButton">
            <button className="play" onClick={(event) => {event.preventDefault()
                                                               this.play()}  }>Play</button>
          </div>
          <div className = "game dnone">
          <ul className = "aboutGame">
              <li className = "abgl">Your Address:<p className = "num">{this.state.Address}</p></li>
              <li className = "abgl">Players:<p className = "num">{this.state.Players}</p></li>
              <li className = "abgl">Invested:<p className = "num">{this.state.Invested}</p></li>
              <li className = "abgl">Paid Out:<p className = "num">{this.state.PaidOut}</p></li>
              <li className = "abgl">Animals:<p className = "num">{this.state.Animals}</p></li>
            </ul>
            <form className = "allani">
            <div className = "animalsp">
              <div className = "animal chicken"><div className = "name">Chicken</div><img className = "image" src = "./Chicken.jpeg"/><button className="buy" onClick={(event) => {event.preventDefault()
                                                                 this.buy()}  }>buy</button><input type="number" name="chnumber" onChange={this.handleChange()}/></div>

              <div className = "animal pig"><div className = "name">Pig</div><img className = "image" src = "./Pig.jpeg"/><button className="buy" onClick={(event) => {event.preventDefault()
                                                                 this.buy()}  }>buy</button><input type="number" name="pgnumber" onChange={this.handleChange()}/></div>
              <div className = "invest"></div>
            </div>
            <div className = "animalsp">
              <div className = "animal sheep"><div className = "name">Sheep</div><img className = "image" src = "./Sheep.jpeg"/><button className="buy" onClick={(event) => {event.preventDefault()
